{                    tempCale = calcolaData(dataInizio, 12, fineMese, giornoMese);                    inserisciScadenza(conn, importoScadenza, tempCale.getTime());                }//                }                return true;            } else {                javax.swing.JOptionPane.showMessageDialog(null, "Impossibile trovare il tipo di pagamento");                //storico                Storico.scrivi("Genera Scadenze Errore Pagamento", "Documento = " + documento_tipo + "/" + documento_serie + "/" + documento_numero + "/" + documento_anno + ", Pagamento = " + pagamento_tipo + ", Importo documento = " + documento_importo);                return false;            }        } catch (Exception err) {            err.printStackTrace();            return false;        }    }    static public GregorianCalendar calcolaData(java.util.Date dataInizio, int mesi, boolean fineMese) {        return (calcolaData(dataInizio, mesi, fineMese, null));    }    static public GregorianCalendar calcolaData(java.util.Date dataInizio, int mesi, boolean fineMese, Byte giornoMese) {        Calendar tempCale = GregorianCalendar.getInstance();        tempCale.setTime(dataInizio);                System.out.println("\nmesi = "+mesi+" - giornoMese = "+giornoMese+" - fineMese = "+fineMese);                    if (fineMese == false && giornoMese == null) {            //calcolo a giorni commerciali            System.out.println("22-PRIMA tempCale = "+((GregorianCalendar) tempCale).getTime());                        if (mesi == 99) { //45                tempCale.add(java.util.Calendar.MONTH, 1);                tempCale.add(java.util.Calendar.DAY_OF_MONTH, 15);            } else if (mesi == 98) { //75                tempCale.add(java.util.Calendar.MONTH, 2);                tempCale.add(java.util.Calendar.DAY_OF_MONTH, 15);            } else if (mesi == 97) { //15                tempCale.add(java.util.Calendar.DAY_OF_MONTH, 15);                       } else { //other                tempCale.add(java.util.Calendar.MONTH, mesi);                        }        } else if (giornoMese != null) {                                    //calcolo al giorno specificato            //controllo che il giorno della fattura sia inferiore al giorno del pagamento altrimenti devo aggiungere piu' giorni            if (fineMese) {                tempCale.set(tempCale.DAY_OF_MONTH, giornoMese.byteValue());                if (mesi == 99) { //45                        tempCale.add(java.util.Calendar.MONTH, 2);                        tempCale.add(java.util.Calendar.DAY_OF_MONTH, 15);                        } else if (mesi == 98) { //75                            tempCale.add(java.util.Calendar.MONTH, 3);                            tempCale.add(java.util.Calendar.DAY_OF_MONTH, 15);                            } else if (mesi == 97) { //15                                tempCale.add(java.util.Calendar.DAY_OF_MONTH, 15);                                        } else { //other                                tempCale.add(java.util.Calendar.MONTH, mesi +1);                                        }                            int giorniMese = tempCale.getActualMaximum(tempCale.DAY_OF_MONTH);            tempCale.set(tempCale.DAY_OF_MONTH, giorniMese);            } else {                if (tempCale.get(tempCale.DAY_OF_MONTH) > giornoMese.byteValue()) {                    tempCale.set(tempCale.DAY_OF_MONTH, giornoMese.byteValue());                                       if (mesi == 99) { //45                        tempCale.add(java.util.Calendar.MONTH, 2);                        tempCale.add(java.util.Calendar.DAY_OF_MONTH, 15);                        } else if (mesi == 98) { //75                            tempCale.add(java.util.Calendar.MONTH, 3);                            tempCale.add(java.util.Calendar.DAY_OF_MONTH, 15);                            } else if (mesi == 97) { //15                            tempCale.add(java.util.Calendar.DAY_OF_MONTH, 15);                                        }  else { //other                                tempCale.add(java.util.Calendar.MONTH, mesi +1);                                        }                       } else {                    tempCale.set(tempCale.DAY_OF_MONTH, giornoMese.byteValue());                    if (mesi == 99) { //45                        tempCale.add(java.util.Calendar.MONTH, 1);                        tempCale.add(java.util.Calendar.DAY_OF_MONTH, 15);                    } else if (mesi == 98) { //75                        tempCale.add(java.util.Calendar.MONTH, 2);                        tempCale.add(java.util.Calendar.DAY_OF_MONTH, 15);                    } else if (mesi == 97) { //15                            tempCale.add(java.util.Calendar.DAY_OF_MONTH, 15);                                } else { //other                        tempCale.add(java.util.Calendar.MONTH, mesi);                                }                }            }        } else {            //calcolo a fine mese            //System.out.println("Days in this month " + calendar.getActualMaximum(Calendar.DAY_OF_MONTH));            //controllo se generare nel mese corrente o quello standard successivo                        System.out.println("33-PRIMA tempCale = "+((GregorianCalendar) tempCale).getTime());            if (mesi == 99) { //45                tempCale.add(java.util.Calendar.MONTH, 1);                tempCale.add(java.util.Calendar.DAY_OF_MONTH, 15);            } else if (mesi == 98) { //75                tempCale.add(java.util.Calendar.MONTH, 2);                tempCale.add(java.util.Calendar.DAY_OF_MONTH, 15);            } else if (mesi == 97) { //15                tempCale.add(java.util.Calendar.DAY_OF_MONTH, 15);            } else {                tempCale.add(java.util.Calendar.MONTH, mesi);                        }                     int giorniMese = tempCale.getActualMaximum(tempCale.DAY_OF_MONTH);            tempCale.set(tempCale.DAY_OF_MONTH, giorniMese);                        System.out.println("33-DOPO tempCale = "+((GregorianCalendar) tempCale).getTime());        }        return (GregorianCalendar) tempCale;    }    private void inserisciScadenza(double importo, java.util.Date scadenza) {        inserisciScadenza(null, importo, scadenza);    }        private void inserisciScadenza(Connection conn, double importo, java.util.Date scadenza) {        //per la quadratura aggiungo alla prima scadenza l'importo della quadratura        importo = it.tnx.Util.round(importo + quadratura, 2);        quadratura = 0;        String sql = "";        try {            //genero una sola scadenza dato che il pagamento ??? di tipo IMMEDAITO            sql = "insert into scadenze (documento_tipo";            sql += ",documento_serie";            sql += ",documento_numero";            sql += ",documento_anno";            sql += ",data_scadenza";            sql += ",pagata";            sql += ",importo";            sql += ",numero) values (";            //valori            sql += Db.pc(this.documento_tipo, Types.VARCHAR);            sql += "," + Db.pc(this.documento_serie, Types.VARCHAR);            sql += "," + Db.pc(this.documento_numero, Types.INTEGER);            sql += "," + Db.pc(this.documento_anno, Types.INTEGER);            //sql += "," + Db.pc(Db.formatDataMysql(java.util.Calendar.getInstance().getTime()), Types.DATE);            sql += "," + Db.pc(Db.formatDataMysql(scadenza), Types.DATE);            //sql += "," + Db.pc("N", Types.VARCHAR);            //metto come defautl a pagata S, se arrivano insoluti si vanno ad inserire            if (this.flag_pagata == true) {                sql += "," + Db.pc("S", Types.VARCHAR);            } else {                sql += "," + Db.pc("N", Types.VARCHAR);            }            sql += "," + Db.pc(importo, Types.DOUBLE);            sql += "," + Db.pc(this.numero, Types.INTEGER);            sql += ")";            //storico            Storico.scrivi(conn, "Genera Scadenze Inserisci", "Documento = " + documento_tipo + "/" + documento_serie + "/" + documento_numero + "/" + documento_anno + ", Importo = " + importo + ", Numero = " + numero + ", Data = " + scadenza);                       Db.executeSql(conn, sql);            this.numero++;        } catch (Exception err) {            err.printStackTrace();        }    }    ResultSet getResultSetDocumento() {        String sql = "";        if (documento_tipo.equals(Db.TIPO_DOCUMENTO_DDT)) {            sql = "Select * from test_ddt";        } else if (documento_tipo.equals(Db.TIPO_DOCUMENTO_FATTURA)) {            sql = "Select * from test_fatt";        } else if (documento_tipo.equals(Db.TIPO_DOCUMENTO_FATTURA_RICEVUTA)) {            sql = "Select * from test_fatt_acquisto";        } else if (documento_tipo.equals(Db.TIPO_DOCUMENTO_ORDINE)) {            sql = "Select * from test_ordi";        } else if (documento_tipo.equals(Db.TIPO_DOCUMENTO_ORDINE_ACQUISTO)) {            sql = "Select * from test_ordi_acquisto";        }        sql += " where serie = " + Db.pc(documento_serie, Types.VARCHAR);        sql += " and numero = " + Db.pc(documento_numero, Types.INTEGER);        sql += " and anno = " + Db.pc(documento_anno, Types.INTEGER);        if (documento_tipo.equals(Db.TIPO_DOCUMENTO_FATTURA)) {            sql += " and IFNULL(tipo_fattura,0) != 7";        }        ResultSet tempDocu = Db.openResultSet(conn, sql);        try {            tempDocu.next();        } catch (Exception err) {            err.printStackTrace();        }        return tempDocu;    }    public static boolean controllaTotali(String sql, JInternalFrame frame) {        try {            Vector fattureDiverse = new Vector();            Vector fattureDiverseSerie = new Vector();            Vector fattureDiverseAnno = new Vector();            Vector fattureDiverseNumero = new Vector();            ResultSet temp = Db.openResultSet(sql);            String oldKey = "-1";            String currentKey = "";            String currentKeySerie = "";            String currentKeyAnno = "";            String currentKeyNumero = "";            double totaleFattura = 0;            double totaleScadenze = 0;            String serie;            int numero;            int anno;            while (temp.next()) {                currentKey = temp.getString("test_fatt.numero");                currentKeySerie = temp.getString("test_fatt.serie");                currentKeyAnno = temp.getString("test_fatt.anno");                currentKeyNumero = temp.getString("test_fatt.numero");                if (oldKey.equalsIgnoreCase(currentKey) || oldKey.equals("-1")) {                    //sommo                    totaleFattura = temp.getDouble("test_fatt.totale");                    totaleScadenze += temp.getDouble("scadenze.importo");                } else {                    if (totaleFattura != totaleScadenze) {                        fattureDiverse.add(currentKey);                        fattureDiverseSerie.add(currentKeySerie);                        fattureDiverseAnno.add(currentKeyAnno);                        fattureDiverseNumero.add(currentKeyNumero);                    }                    //azzero                    totaleFattura = 0;                    totaleScadenze = 0;                }                oldKey = temp.getString("test_fatt.numero");            }            if (fattureDiverse.size() > 0) {                javax.swing.JOptionPane.showMessageDialog(null, "Ci sono delle scadenze non congruenti con le fatture :" + fattureDiverse.toString());                for (int i = 0; i < fattureDiverse.size(); i++) {                    serie = fattureDiverseSerie.get(i).toString();                    numero = Integer.parseInt(fattureDiverseNumero.get(i).toString());                    anno = Integer.parseInt(fattureDiverseAnno.get(i).toString());                    Scadenze tempScad = new Scadenze(Db.TIPO_DOCUMENTO_FATTURA, serie, numero, anno, null);                    frmPagaPart frm = new frmPagaPart(tempScad, null);                    main.getPadre().openFrame(frm, 450, 400, 200 + (i * 10), 100 + (i * 10));                }                return false;            }            return true;        } catch (Exception err) {            err.printStackTrace();            return false;        }    }}