ew();        //azzero banca e filaile        this.labBancAbi.setText("");        this.labBancCab.setText("");        java.sql.Statement stat;        ResultSet resu;        //apre il resultset per ultimo +1        try {            stat = Db.getConn().createStatement();            String sql = "select codice from clie_forn order by codice desc limit 1";            resu = stat.executeQuery(sql);            if (resu.next() == true) {                this.texCodi.setText(String.valueOf(resu.getInt(1) + 1));            } else {                this.texCodi.setText("1");            }        } catch (Exception err) {            javax.swing.JOptionPane.showMessageDialog(null, err.toString());        }        this.comPaese.dbTrovaRiga("ITALY");        //se cc metto usa        if (main.getPersonalContain(main.PERSONAL_CHIANTICASHMERE)) {            this.comPaese.dbTrovaRiga("USA");        }        if (main.iniPrezziCliente == true) {            try {                if (this.texCodi.getText().trim().length() > 0) {                    Cliente c = new Cliente(Integer.parseInt(this.texCodi.getText()));                    String listinoCliente = c.getListinoCliente(true);                    this.comList.dbRefreshItems();                    this.comList.dbTrovaKey(listinoCliente);                    this.texCodiList.setText(listinoCliente);                }            } catch (Exception err) {                err.printStackTrace();            }        }        comTipo.requestFocusInWindow();    }//GEN-LAST:event_butNewActionPerformed    private void texSearchKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_texSearchKeyPressed    }//GEN-LAST:event_texSearchKeyPressed    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed        texSearch.setText("");        String sql = "select " + getCampiGriglia() + " from clie_forn";//        java.util.Hashtable colsWidthPerc 